package ManishLokesh.Neptune.v1.GetOrderFromIRCTC.Controller;import ManishLokesh.Neptune.AuthController.JwtUtil;import ManishLokesh.Neptune.ResponseDTO.ResponseDTO;import ManishLokesh.Neptune.v1.GetOrderFromIRCTC.RequestBody.IRCTCRequestBody;import ManishLokesh.Neptune.v1.GetOrderFromIRCTC.Service.OrderService;import com.fasterxml.jackson.core.JsonProcessingException;import org.springframework.beans.factory.annotation.Autowired;import org.springframework.http.HttpHeaders;import org.springframework.http.HttpStatus;import org.springframework.http.ResponseEntity;import org.springframework.web.bind.annotation.PostMapping;import org.springframework.web.bind.annotation.RequestBody;import org.springframework.web.bind.annotation.RequestHeader;import org.springframework.web.bind.annotation.RestController;@RestControllerpublic class GetIRCTCOrderController {    @Autowired    public JwtUtil jwtUtil;    @Autowired    public OrderService service;    @PostMapping("/order")    public ResponseEntity<?>getOrder(@RequestBody IRCTCRequestBody irctcRequestBody, @RequestHeader(HttpHeaders.AUTHORIZATION) String auth){        String token = auth.replace("Bearer","");        if(jwtUtil.validateToken(token)){            try{                return this.service.OrderCapture(irctcRequestBody);            }catch (JsonProcessingException e){                return new ResponseEntity<>(new ResponseDTO("failure","Something went Wrong",null),                        HttpStatus.INTERNAL_SERVER_ERROR);            }        }        return new ResponseEntity<>(new ResponseDTO("failure","Not Authorise to Access",null),                HttpStatus.UNAUTHORIZED);    }}